use sabiduria;



























DROP VIEW IF EXISTS UsuarioVista;
CREATE VIEW UsuarioVista AS
SELECT u.*, p.Clave
FROM  Usuario u, PermisoPorUsuario pu, Permiso p
WHERE u.UsuarioId = pu.UsuarioId AND p.PermisoId = pu.PermisoId;

DROP VIEW IF EXISTS AutorVista;
CREATE VIEW AutorVista AS
SELECT a.*, p.Abreviacion
FROM Autor a, Prefijo p
WHERE p.PrefijoId = a.PrefijoId;

DROP VIEW IF EXISTS FuenteVista;
CREATE VIEW FuenteVista AS
SELECT f.*, tf.Nombre as NombreTipoFuente
FROM TipoFuente tf, Fuente f
WHERE f.TipoFuenteId = tf.TipoFuenteId;

DROP VIEW IF EXISTS PermisoVista;
CREATE VIEW PermisoVista AS
SELECT p.*, a.Nombre as NombreAplicacion
FROM Permiso p, Aplicacion a
WHERE p.AplicacionId = a.AplicacionId;

DROP VIEW IF EXISTS InformacionVista;
CREATE VIEW InformacionVista AS
SELECT i.InformacionId, i.Contenido, i.Archivo, i.TipoInformacionId, i.OrigenInformacionId, i.FuenteId, i.TemaId, t.Nombre as NombreTema,
		f.Nombre as NombreFuente, ti.Nombre as NombreTipoInformacion, tf.Nombre as NombreTipoFuente, tf.TipoFuenteId, i.NombreArchivo, i.ExtensionArchivo
FROM Informacion i
INNER JOIN Tema t ON i.TemaId = t.TemaId
INNER JOIN TipoInformacion ti ON i.TipoInformacionId = ti.TipoInformacionId,
	Fuente f
INNER JOIN TipoFuente tf ON tf.TipoFuenteId = f.TipoFuenteId
WHERE i.FuenteId = f.FuenteId;

